/*==============================================================*/
/* View: V_ROLE_JX                                              */
/*==============================================================*/
CREATE OR REPLACE VIEW V_ROLE_JX AS
SELECT B1.ID 

 USER_ID, B3.ROLE_ID, B3.DPRTCODE, B3.FJJX, B3.FJZCH, B3.XLH, B3.FDJSL
  FROM T_USER B1
  JOIN T_USER2ROLE_JX B2
    ON B1.ID 

 = B2.USER_ID
  JOIN (SELECT DISTINCT ROLE_ID, DPRTCODE, FJJX, FJZCH, XLH, FDJSL FROM
       (SELECT T1.ROLE_ID, T1.DPRTCODE, T1.FJJX, NVL(T2.FJZCH, '-') FJZCH, T2.XLH, T2.FDJSL
          FROM T_ROLE2JX T1
          LEFT JOIN D_007 T2
            ON T1.DPRTCODE = T2.DPRTCODE
           AND T1.FJZCH = T2.FJZCH
           AND T2.ZT = 1
         WHERE T1.LX = 2
        UNION
        SELECT A1.ROLE_ID, A1.DPRTCODE, A1.FJJX, NVL(A2.FJZCH, '-') FJZCH, A2.XLH, A2.FDJSL
          FROM T_ROLE2JX A1
          LEFT JOIN D_007 A2
            ON A1.DPRTCODE = A2.DPRTCODE
           AND A1.FJJX = A2.FJJX
           AND (A2.ZT = 1 OR A2.FJZCH IS NULL)
           AND A1.LX = 1)
          ) B3
    ON B2.ROLE_ID = B3.ROLE_ID
  JOIN D_004 B4
    ON B4.DPRTCODE = B3.DPRTCODE
   AND B4.ZT = 1
   AND B4.FJJX = B3.FJJX
WITH READ ONLY;

/*==============================================================*/
/* View: V_ROLE_WH                                              */
/*==============================================================*/
CREATE OR REPLACE VIEW V_ROLE_WH AS
SELECT B1.ID AS USER_ID, B3.ROLE_ID, B3.CK_ID
  FROM T_USER B1
  JOIN T_USER2ROLE_CK B2
    ON B1.ID = B2.USER_ID
  JOIN T_ROLE2WH B3
    ON B2.ROLE_ID = B3.ROLE_ID
  JOIN D_009 B4 ON B3.CK_ID = B4.ID AND B4.ZT =1
WITH READ ONLY;

/*==============================================================*/
/* View: V_SPENTDETAIL                                          */
/*==============================================================*/
CREATE OR REPLACE VIEW V_SPENTDETAIL AS
SELECT CASE
         WHEN TO_CHAR(E.BOOKONDATE_, 'yyyymmdd') <= '20161116' THEN
          TO_CHAR(E.BOOKONDATE_, 'yyyymmdd')
         WHEN D.CHANGDATE_ IS NULL THEN
          TO_CHAR(E.BOOKONDATE_, 'yyyymmdd')
         ELSE
          TO_CHAR(D.CHANGDATE_, 'yyyymmdd')
       END GROUPBYDATE,
       E.ID_ AS EMPWORKID,
       E.RID_ AS RID,
       E.WID_ AS WID,
       E.TOEID_ AS EID,
       E.TOENAME_ AS ENAME,
       E.EMPTRADE_ AS DEPT,
       (SELECT MAX(R.AIRCRAFTTAILNUMBER_)
          FROM PBS_REVISION R
         WHERE R.RID_ = E.RID_) AS AIRNUMBER,
       (SELECT MAX(O.OPERATIONDESCRIPTION_)
          FROM PBS_OPERATION O
         WHERE O.WID_ = E.WID_
           AND O.WORKCENTER_ = E.TIMINGTRADE_) AS OPERATIONDESC,
       (SELECT MAX(W.CARDNUMBER_) FROM PBS_WORK W WHERE W.WID_ = E.WID_) AS CARDNUMBER,
       E.BOOKONDATE_ AS STARTDATETEMP,
       NVL(D.CHANGDATE_, E.BOOKONDATE_) AS CHANGEDATE,
       NVL(E.BOOKOFFDATE_, E.LASTCOUNTDATE_) AS ENDDATE,
       'H' AS UNIT,
       E.TIMINGTRADE_ AS TIMINGTRADE,
       '' AS HEADCOUNT,
       NVL(D.SPENTHOURS_, 0) AS SPENTHOUR,
       E.SUBSTATUS_ AS SUBSTATUS,
       D.CHANGETYPE_ AS BOOKOFFTYPE,
       REPLACE(SUBSTR(LOWER(E.EMPTRADE_), 0, INSTR(E.EMPTRADE_, '[')),
               ' ',
               '') AS EMPTRADE
  FROM PBS_EMPWORK E
  LEFT JOIN PBS_EMPWORKDETAIL D
    ON E.ID_ = D.EMPWORKID_
   AND E.RID_ = D.RID_
 WHERE E.SUBSTATUS_ != '0'
WITH READ ONLY;

